interface PlaylistLikeBase {
  created_at: Date
  type: PlaylistLikeType
  user: User
  uuid: string
  caption: null
  playlist?: Playlist
  system_playlist?: SystemPlaylist
}

export type PlaylistLike = PlaylistLikeBase &
  (
    | { playlist: Playlist; system_playlist?: never }
    | { system_playlist: SystemPlaylist; playlist?: never }
  )

export interface BasePlaylist {
  id: number | string
  title: string
  tracks?: PartialTrack[]
}

export interface ExactPlaylist extends BasePlaylist {
  tracks: Track[]
}

export class LocalPlaylist {
  id: string
  title: string
  tracks: Track[] = []

  constructor(name: string) {
    this.id = name
    this.title = name
  }
}

export interface Playlist extends BasePlaylist {
  artwork_url: null | string
  created_at: Date
  duration: number
  id: number
  kind: PlaylistKind
  last_modified: Date
  likes_count: number
  managed_by_feeds: boolean
  permalink: string
  permalink_url: string
  public: boolean
  reposts_count: number
  secret_token: null
  sharing: Sharing
  title: string
  track_count: number
  uri: string
  user_id: number
  set_type: string
  is_album: boolean
  published_at: Date | null
  release_date: Date | null
  display_date: Date
  user: User
  tracks?: PartialTrack[]
}

export type PlaylistKind = "playlist"

export interface SystemPlaylist extends BasePlaylist {
  urn: string
  query_urn: null | string
  permalink: string
  permalink_url: string
  title: string
  description: string
  short_title: string
  short_description: null | string
  tracking_feature_name: string
  playlist_type: PlaylistType
  last_updated: Date | null
  artwork_url: string
  calculated_artwork_url: string
  likes_count: number
  seed: Seed | null
  tracks: PartialTrack[]
  is_public: boolean
  made_for: User | null
  user: User
  kind: SystemPlaylistKind
  id: string
}

export type SystemPlaylistKind = "system-playlist"

export type PlaylistType = "PLAYLIST" | "TRACK_STATION"

export interface Seed {
  urn: string
  permalink: string
}

export type PlaylistLikeType = "playlist-like" | "system-playlist-like"

